mapper.select:
SELECT current_step
FROM document d
where doc_id = #{docId}

SELECT step_no
FROM approval_step
WHERE doc_id = #{docId}
  AND approver_id = #{userId}
  AND approver_id != (
    SELECT created_id
    FROM document
    WHERE doc_id = #{docId}
  )

mapper.insert:
INSERT INTO approval_step( doc_id
                         , step_no
                         , approver_id
                         , status
                         , created_id
                         , created_date
                         , updated_id
                         , updated_date)
VALUES ( #{docId}
       , #{approvalStepNo}
       , #{userId}
       , #{approvalStatus}
       , #{userId}
       , DATE_FORMAT(NOW(), '%Y%m%d %H%i%s')
       , #{userId}
       , DATE_FORMAT(NOW(), '%Y%m%d %H%i%s'))

mapper.update:
UPDATE approval_step
SET status= #{status}
WHERE approver_id = #{approverId}
  AND doc_id = #{docId}

UPDATE document
SET updated_id   = #{approverId}
  , updated_date = DATE_FORMAT(NOW(), '%Y%m%d %H%i%s')
  , doc_status   = '04'
WHERE doc_id = #{docId}

UPDATE document
SET updated_id   = #{approverId}
  , updated_date = DATE_FORMAT(NOW(), '%Y%m%d %H%i%s')
  , doc_status   = '03'
WHERE doc_id = #{docId}

UPDATE document
SET current_step = current_step + 1
WHERE doc_id = #{docId}
  AND current_step < total_step;
