mapper.select:
SELECT doc_current_step
FROM document d
where doc_id = #{docId}

SELECT step_no
FROM approval_step
WHERE doc_id = #{docId}
  AND approver_id = #{userId}

mapper.update:
UPDATE approval_step
SET status= #{status}
WHERE approver_id= #{approverId}
  AND doc_id = #{docId}

UPDATE document
SET updated_id = #{approverId}
  , updated_date = DATE_FORMAT(NOW(), '%Y%m%d')
  , doc_status= '03'
WHERE doc_id= #{docId}
