mapper.insert:
INSERT INTO approval_step(
      doc_id
    , step_no
    , approver_id
    , status
    , created_id
    , created_date
    , updated_id
    , updated_date
) VALUES (
       #{docId}
     , #{approvalStepNo}
     , #{userId}
     , #{approvalStatus}
     , #{userId}
     , DATE_FORMAT(NOW(), '%Y%m%d %H%i%s')
     , #{userId}
     , DATE_FORMAT(NOW(), '%Y%m%d %H%i%s')
 )

 INSERT INTO vacation_detail(
         doc_id
        , start_date
        , end_date
        , vacation_type
        , emergency_contact
        , personal_reason
        , vacation_period
        , created_id
        , created_date
        , updated_id
        , updated_date
 ) VALUES (
         #{docId}
        , #{startDate}
        , #{endDate}
        , #{vacationType}
        , #{emergencyContact}
        , #{personalReason}
        , #{vacationPeriod}
        , '관리자'
        , DATE_FORMAT(NOW(), '%Y%m%d %H%i%s')
        , '관리자'
        , DATE_FORMAT(NOW(), '%Y%m%d %H%i%s')
)

mapper.update:
UPDATE approval_step
   SET status= #{status}
 WHERE approver_id= #{approverId}
  AND doc_id = #{docId}
