mapper.insert:
	INSERT INTO approval_request
	     ( doc_id
		 , approval_id
		 , draft_date
		 , department
		 , drafter
		 , approval_title
		 , approval_content
		 , instructions
		 , remark
		 , created_id
		 , created_date
		 , updated_id
		 , updated_date)
  	VALUES 
  		 ( #{docId} 
  		 , #{approval_id}
		 , #{draft_date}
		 , #{department}
		 , #{drafter}
		 , #{approval_title}
		 , #{approval_content}
		 , #{instructions}
		 , #{remark}
		 , #{userId}
		 , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
		 , #{userId}
		 , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'))

	INSERT INTO transport_expense_detail
