		 , vehicle_end
		 , vehicle_purpose
		 , visit_description
		 , expense_amount
		 , remark
		 , created_id
		 , created_date
		 , updated_id
		 , updated_date )
  	VALUES 
  		 ( #{docId} 
  		 , #{expenseDate}
		 , #{vehicleStart}
		 , #{vehicleEnd}
		 , #{vehiclePurpose}
		 , #{visitDescription}
		 , #{expenseAmount}
		 , #{remark}
		 , #{userId}
